	@Test
	public void createTintucTest() {
		// Create an tintuc
		Tintuc tintuc = createMocKTintuc(Integer.valueOf(100) , TIEUDE, NGAYDANG, NOIDUNG);
		// Recover this tintuc
		ResponseEntity<TintucResource> result = this.restTemplate.getForEntity("/tintuc/1", TintucResource.class);
		// The response can't be null
		assertThat(result).isNotNull();
		// The status code must be OK
		assertThat(result.getStatusCode()).isEqualTo(HttpStatus.OK);
		// Response body must not be null
		assertThat(result.getBody()).isNotNull();
		// Assert the Hateoas self link
		Link link = new Link("http://localhost:9999/tintuc/1", Link.REL_SELF);
		assertThat(result.getBody().getId()).isEqualTo(link);
		assertThat(result.getBody().getTieude()).isEqualTo(TIEUDE);
		assertThat(result.getBody().getNgaydang()).isEqualTo(NGAYDANG);
		assertThat(result.getBody().getNoidung()).isEqualTo(NOIDUNG);
		// Try to create the same tintuc, must return a CONFLICT status code
		result = this.restTemplate.postForEntity("/tintuc", tintuc, TintucResource.class);
		// The reponse can't be null
		assertThat(result).isNotNull();
		// The status code must be OK
		assertThat(result.getStatusCode()).isEqualTo(HttpStatus.CONFLICT);
		// Response body must not be null
		assertThat(result.getBody()).isNull();
	}

