	@Test
	public void getTintucsTest() {
		// First call assert no element
		ResponseEntity<PagedResources<TintucResource>> result = this.search("/tintuc");
		// The reponse can't be null
		assertThat(result).isNotNull();
		// The status code must be OK
		assertThat(result.getStatusCode()).isEqualTo(HttpStatus.OK);
		// Response body must not be null
		PagedResources<TintucResource> page = result.getBody();
		assertThat(page).isNotNull();
		assertThat(page.getMetadata()).isNotNull();
		assertThat(page.getMetadata().getTotalElements()).isEqualTo(0);
		assertThat(page.getMetadata().getTotalPages()).isEqualTo(0);
		// By default page size is 20
		assertThat(page.getMetadata().getSize()).isEqualTo(20);

		// Insert 21 tintuc
		for (int i = 1; i < 22; i++) {
			createMocKTintuc(new Integer((int)i),String.valueOf(i),String.valueOf(i),String.valueOf(i));
		}
		result = this.search("/tintuc");
		// Response body must not be null
		page = result.getBody();
		assertThat(page).isNotNull();
		assertThat(page.getMetadata().getTotalElements()).isEqualTo(21);
		assertThat(page.getMetadata().getTotalPages()).isEqualTo(2);

		// Change the number of element per page and assert the page number
		result = this.search("/tintuc?size=3");
		// Response body must not be null
		page = result.getBody();
		assertThat(page).isNotNull();
		assertThat(page.getMetadata().getTotalElements()).isEqualTo(21);
		assertThat(page.getMetadata().getTotalPages()).isEqualTo(7);
	}

	@Test
	public void getTintucsTestFilter() {
		// Create an tintuc
		Tintuc tintuc = createMocKTintuc(Integer.valueOf(100) , TIEUDE, NGAYDANG, NOIDUNG);
		RequestEntity<Tintuc> request = null;
		ResponseEntity<PagedResources<TintucResource>> result = null;
		PagedResources<TintucResource> page = null;
		// Test tieude filter
		// Set the value on entity and update
		tintuc.setTieude("c");
		// Process update
		request = new RequestEntity<Tintuc>(tintuc, HttpMethod.PUT, null);
		this.restTemplate.exchange("/tintuc/1", HttpMethod.PUT, request, getTypeRefVoid());
		// Test tieude filter
		result = this.search("/tintuc?size=20&tieude=c");
		// Response body must not be null
		page = result.getBody();
		assertThat(page).isNotNull();
		assertThat(page.getMetadata().getTotalElements()).isEqualTo(1);
		assertThat(page.getMetadata().getTotalPages()).isEqualTo(1);
		for (TintucResource tintucResource : page.getContent()) {
			assertThat(tintucResource.getTieude()).startsWith("c");
		}
		// Test ngaydang filter
		// Set the value on entity and update
		tintuc.setNgaydang("c");
		// Process update
		request = new RequestEntity<Tintuc>(tintuc, HttpMethod.PUT, null);
		this.restTemplate.exchange("/tintuc/1", HttpMethod.PUT, request, getTypeRefVoid());
		// Test ngaydang filter
		result = this.search("/tintuc?size=20&ngaydang=c");
		// Response body must not be null
		page = result.getBody();
		assertThat(page).isNotNull();
		assertThat(page.getMetadata().getTotalElements()).isEqualTo(1);
		assertThat(page.getMetadata().getTotalPages()).isEqualTo(1);
		for (TintucResource tintucResource : page.getContent()) {
			assertThat(tintucResource.getNgaydang()).startsWith("c");
		}
		// Test noidung filter
		// Set the value on entity and update
		tintuc.setNoidung("c");
		// Process update
		request = new RequestEntity<Tintuc>(tintuc, HttpMethod.PUT, null);
		this.restTemplate.exchange("/tintuc/1", HttpMethod.PUT, request, getTypeRefVoid());
		// Test noidung filter
		result = this.search("/tintuc?size=20&noidung=c");
		// Response body must not be null
		page = result.getBody();
		assertThat(page).isNotNull();
		assertThat(page.getMetadata().getTotalElements()).isEqualTo(1);
		assertThat(page.getMetadata().getTotalPages()).isEqualTo(1);
		for (TintucResource tintucResource : page.getContent()) {
			assertThat(tintucResource.getNoidung()).startsWith("c");
		}
	}

	private Tintuc createMocKTintuc(Integer idtintuc, String tieude, String ngaydang, String noidung) {
		Tintuc tintucMock = new Tintuc();
		tintucMock.setIdtintuc(idtintuc);
		tintucMock.setTieude(tieude);
		tintucMock.setNgaydang(ngaydang);
		tintucMock.setNoidung(noidung);
		this.restTemplate.postForLocation("/tintuc", tintucMock);
		return tintucMock;
	}

